import React, { useEffect, useState } from "react";
import Card from "./Card";
import { connect } from "react-redux";
import { removeFav } from "../redux/actions";

export function Favorites(props) {
  const [charFavorites, setCharFavorites] = useState([]);

  useEffect(() => {
    // Update the charFavorites array whenever myFavorites changes
    const updatedCharFavorites = props.myFavorites.map((personaje) => (
      <Card
        key={personaje.id}
        id={personaje.id}
        name={personaje.name}
        status={personaje.status}
        species={personaje.species}
        gender={personaje.gender}
        origin={personaje.origin.name}
        image={personaje.image}
        onClose={props.onClose}
      />
    ));
    setCharFavorites(updatedCharFavorites);
  }, [props.myFavorites]);

  return <div>{charFavorites}</div>;
}

export function mapStateToProps(state) {
  return { myFavorites: state.myFavorites };
}

export default connect(mapStateToProps, null)(Favorites);
