import './App.css';
import React from 'react';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx';
import About from './components/About/About';
import axios from 'axios';
import { Routes, Route} from "react-router-dom"

function App() {

   const [characters, setCharacters] = React.useState([])

   function onSearch(id) {
      axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
         if (data.name) {
            if (characters.some((character) => character.id === data.id)) {
               window.alert('Ya hay un personaje agregado con ese ID')
            } else {
               setCharacters((oldChars) => [...oldChars, data]);
            }
         } else {
            window.alert('¡No hay personajes con este ID!')
         }
      });
   }

   const numRandom = () => {
      return Math.floor(Math.random() * 827);
    }
    
   function randomChar () {
      const idRandom = numRandom()
      axios(`https://rickandmortyapi.com/api/character/${idRandom}`).then(({ data }) => {
         if (data.name) {
            if (characters.some((character) => character.id === data.id)) {
               window.alert('Ya hay un personaje agregado con ese ID')
            } else {
               setCharacters((oldChars) => [...oldChars, data]);
            }
         } else {
            window.alert('¡No hay personajes con este ID!')
         }
      });
   }

   const onClose = (id) => {
      const updateCharacters = characters.filter((character) => character.id !== id)
      setCharacters(updateCharacters)
   }

   return (
      <div className='App'>
         {/* <Nav onSearch={onSearch} randomChar={randomChar}/>
         <Cards onClose={onClose} characters={characters} /> */}
         <About />
      </div>
   );
}

export default App;
