import { ADD_FAV } from "./actions"
import { REMOVE_FAV } from "./actions"
import { FILTER } from "./actions"
import { ORDER } from "./actions"

const initialState = {
    myFavorites: [],
    allCharacters: [],
}

const rootReducer = (state=initialState, action) => {
    switch(action.type) {
        case ADD_FAV:
            return {...state, allCharacters: [...state.allCharacters, action.payload], myFavorites: [...state.myFavorites, action.payload]}
        case REMOVE_FAV:
            return {...state, myFavorites: state.myFavorites.filter((character) => (character.id) !== action.payload)}
        case FILTER:
            return {...state, myFavorites: state.allCharacters.filter((character) => (character.gender) === action.payload)}
        case ORDER:
            let orderedCharacters = [...state.allCharacters]
            if (action.payload === "A") {
                orderedCharacters.sort((a, b) => a.id - b.id)
            } else if (action.payload === "D") {
                orderedCharacters.sort((a, b) => b.id - a.id)
            }
            return {...state, allCharacters: orderedCharacters}
        default:
            return state
    }
}

export default rootReducer;
