import React, { useState } from "react";
import validate from "../validation";
import styles from '../Form.modules.css'
import { Link } from "react-router-dom"

export default function Form (props) {

    const [userData, setuserData] = useState ({
        email:"",
        password:""
    })

    const [errors, setErrors] = useState ({
        email:"",
        password:""
    })

    const handleChange = event => {
        setuserData({...userData, [event.target.name]: event.target.value})
        setErrors(validate({...userData, [event.target.name]: event.target.value}))
    }

    const handleSubmit = event => {
        event.preventDefault(props.login(userData))
    }

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label htmlFor="email">EMAIL:</label>
                <input placeholder="Escribe tu email..." name="email" type="text" value={userData.email} onChange={handleChange} className={errors.email && 'warning'}></input>
                <p className={styles.danger}>{errors.email}</p>
            </div>
            <div>
                <label htmlFor="password">PASSWORD:</label>
                <input placeholder="Escribe tu password..." name="password" type="text" value={userData.password} onChange={handleChange} className={errors.password && 'warning'}></input>
                <p className="danger">{errors.password}</p>
            </div>
            <div>
                <button type="submit">LOG IN</button>
            </div>
        </form>
    )
}
