import './App.css';
import React, { useState, useEffect } from 'react';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx';
import About from './components/About';
import Detail from './components/Detail';
import Error from './components/Error';
import Form from './components/Form/Form';
import Favorites from './components/Favorites';
import axios from 'axios';
import { removeFav } from './redux/actions';
import { connect } from 'react-redux';

import { Routes, Route, useLocation, useNavigate } from "react-router-dom"

function App() {

   const [characters, setCharacters] = React.useState([])
   const location = useLocation()
   const [access, setAccess] = useState(false)
   const navigate = useNavigate()
   const EMAIL = 'lucaszibaitis@gmail.com'
   const PASSWORD = 'anvers335'

   function login(userData) {
      if (userData.email === EMAIL && userData.password === PASSWORD) {
         setAccess(true)
         navigate('/home')
      } else {
         window.alert('El email y/o la contraseña son incorrectos')
      }
   }

   const logout = () => {
      setAccess(false)
   }
   
   useEffect(() => {
      !access && navigate('/')
   }, [access])

   function onSearch(id) {
      axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
         if (data.name) {
            if (characters.some((character) => character.id === data.id)) {
               window.alert('Ya hay un personaje agregado con ese ID')
            } else {
               setCharacters((oldChars) => [...oldChars, data]);
            }
         } else {
            window.alert('¡No hay personajes con este ID!')
         }
      });
   }

   const numRandom = () => {
      return Math.floor(Math.random() * 827);
    }

   function randomChar () {
      const idRandom = numRandom()
      axios(`https://rickandmortyapi.com/api/character/${idRandom}`).then(({ data }) => {
         if (data.name) {
            if (characters.some((character) => character.id === data.id)) {
               window.alert('Ya hay un personaje agregado con ese ID')
            } else {
               setCharacters((oldChars) => [...oldChars, data]);
            }
         } else {
            window.alert('¡No hay personajes con este ID!')
         }
      });
   }

   const onClose = (id) => {
      const updateCharacters = characters.filter((character) => character.id !== id)
      setCharacters(updateCharacters)
      props.removeFav(id)
   }

   return (
      <div className='App'>
         {location.pathname !== "/" ? <Nav onSearch={onSearch} randomChar={randomChar} logout={logout}/> : null}
         <Routes>
            <Route path="/" element={<Form login={login} />}/>
            <Route path="/home" element={<Cards onClose={onClose} characters={characters}/>}/>
            <Route path="favorites" element={<Favorites/>}/>
            <Route path="/about" element={<About />}/>
            <Route path="/detail/:id" element={<Detail />}/>
            <Route path="*" element={<Error />}/>
         </Routes>
      </div>
   );
}

export default App;
