import React from "react";
import { useEffect, useState } from "react";
import Card from "./Card";
import { connect, useDispatch } from "react-redux";
import { filterCards, orderCards } from "../redux/actions";

export function Favorites(props) {

    const myFavorites = props.myFavorites

    const [filterValue, setFilterValue] = useState("all"); // Estado para el valor del filtro

    const charFavorites = myFavorites
        .filter(personaje => filterValue === "all" || personaje.gender === filterValue)
        .map(personaje => (
            <Card
                key={personaje.id}
                id={personaje.id}
                name={personaje.name}
                status={personaje.status}
                species={personaje.species}
                gender={personaje.gender}
                origin={personaje.origin}
                image={personaje.image}
                isFavoriteView={true}
            />
        ));

    const dispatch = useDispatch()

    const handleOrder = (e) => {
        dispatch(orderCards(e.target.value))
    }

    const handleFilter = (e) => {
        dispatch(filterCards(e.target.value))
    }

    return (
        <div>
            <select onChange={handleOrder}>
              <option value="A">Ascendente</option>
              <option value="D">Descendente</option>    
            </select>
            <select onChange={handleFilter}>
                <option value="all">Mostrar Todos</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Genderless">Genderless</option>
                <option value="unknown">unknown</option>
            </select>
            {charFavorites}
        </div>
    )
}

export function mapStateToProps(state) {
    return {myFavorites: state.myFavorites}
}

export default connect(mapStateToProps, null)(Favorites)
