import React from "react";
import Card from "./Card";
import { connect } from "react-redux";
import { addFav, removeFav } from "../redux/actions";

export function Favorites(props) {

    const myFavorites = props.myFavorites

    const charFavorites = myFavorites.map((personaje) => 
        <Card 
        key={personaje.id}
        id={personaje.id}
        name={personaje.name}
        status={personaje.status}
        species={personaje.species}
        gender={personaje.gender}
        origin={personaje.origin.name}
        image={personaje.image}
        onClose={props.removeFav(personaje.id)}/>)

    return (
        <div>
            {charFavorites}
        </div>
    )
}

export function mapStateToProps(state) {
    return {myFavorites: state.myFavorites}
 }

 export function mapDispatchToProps(dispatch) {
    return ({addFav: (character) => {dispatch(addFav(character))}, removeFav: (id) => {dispatch(removeFav(id))}})
 }

 export default connect(mapStateToProps, mapDispatchToProps)(Favorites)
