import { ADD_FAV } from "./actions"
import { REMOVE_FAV } from "./actions"

const initialState = {
    myFavourites: []
}

const rootReducer = (state=initialState, action) => {
    switch(action.type) {
        case ADD_FAV:
            return {...state, myFavourites: [...state.myFavourites, action.payload]}
        case REMOVE_FAV:
            return {...state, myFavourites: state.filter((character) => (character.id).toString() !== action.payload)}
        default:
            return state
    }
}

export default rootReducer;
