import { Link } from "react-router-dom"
import { connect } from 'react-redux';
import { addFav, removeFav } from "../redux/actions";
import { useState } from "react";
import { useEffect } from "react";

export default function Card(props) {

   const myFavorites = props.myFavorites

   const [isFav, setIsFav] = useState(false)

   const handleFavorite = () => {
      if (isFav === true) {
         setIsFav(false)
         props.removeFav(props.id)
      } else if (isFav === false) {
         setIsFav(true)
         props.addFav(props)
      }
   }

   // useEffect(() => {
   //    myFavorites.forEach((fav) => {
   //       if (fav.id === props.id) {
   //          setIsFav(true);
   //       }
   //    });
   // }, [myFavorites]);

   return (
      <div>
         {isFav ? (<button onClick={handleFavorite}>‚ù§Ô∏è</button>) : (<button onClick={handleFavorite}>ü§ç</button>)}
         <button onClick={() => props.onClose(props.id)}>X</button>
         <Link to={`/detail/${props.id}`}>
         <h2>{props.name}</h2>
         </Link>
         <h2>{props.status}</h2>
         <h2>{props.species}</h2>
         <h2>{props.gender}</h2>
         <h2>{props.origin}</h2>
         <img src={props.image} alt='' />
      </div>
   );
}

export function mapDispatchToProps(dispatch) {
   return ({addFav: (character) => {dispatch(addFav(character))}, removeFav: (id) => {dispatch(removeFav(id))}})
}

export function mapStateToProps(state) {
   return {
      myFavorites: state.myFavorites
   }
}
