import React, { useState } from "react";
import validate from "./validation";
import { Link } from "react-router-dom"

export default function Form () {

    const [userData, setuserData] = useState ({
        email:"",
        password:""
    })

    const [errors, setErrors] = useState ({
        email:"",
        password:""
    })

    const handleChange = event => {
        setuserData({...userData, [event.target.name]: event.target.value})
        setErrors(validate({...userData, [event.target.name]: event.target.value}))
    }

    return (
        <form>
            <div>
                <label htmlFor="email">EMAIL:</label>
                <input placeholder="Escribe tu email..." name="email" type="text" value={userData.email} onChange={handleChange}></input>
            </div>
            <div>
                <label htmlFor="password">PASSWORD:</label>
                <input placeholder="Escribe tu password..." name="password" type="text" value={userData.password} onChange={handleChange}></input>
            </div>
            <div>
                <button type="submit">ENVIAR</button>
            </div>
        </form>
    )
}
