import { ADD_FAV } from "./actions"
import { REMOVE_FAV } from "./actions"
import { FILTER } from "./actions"
import { ORDER } from "./actions"

const initialState = {
    myFavorites: [],
}

const rootReducer = (state=initialState, action) => {
    switch(action.type) {
        case ADD_FAV:
            return {...state, myFavorites: [...state.myFavorites, action.payload]}
        case REMOVE_FAV:
            return {...state, myFavorites: state.myFavorites.filter((character) => (character.id) !== action.payload)}
        case FILTER:
            const filteredCharacters = state.myFavorites.filter((character) => (character.gender) === action.payload)
            return {...state, myFavorites: filteredCharacters}
        case ORDER:
            let orderedCharacters = [...state.myFavorites]
            if (action.payload === "A") {
                orderedCharacters.sort((a, b) => a.id - b.id)
            } else if (action.payload === "D") {
                orderedCharacters.sort((a, b) => b.id - a.id)
            }
            return {...state, myFavorites: orderedCharacters}
        default:
            return state
    }
}

export default rootReducer;
