import { ADD_FAV } from "./actions"
import { REMOVE_FAV } from "./actions"
import { FILTER } from "./actions"
import { ORDER } from "./actions"

const initialState = {
    myFavorites: [],
    allCharacters: []
}

const rootReducer = (state=initialState, action) => {
    switch(action.type) {
        case ADD_FAV:
            return {...state, allCharacters: [...state.allCharacters, action.payload], myFavorites: [...state.myFavorites, action.payload]}
        case REMOVE_FAV:
            return {...state, myFavorites: state.myFavorites.filter((character) => (character.id) !== action.payload)}
        case FILTER:
            return {...state, allCharacters: state.allCharacters.filter((character) => (character.gender) === action.payload)}
        default:
            return state
    }
}

export default rootReducer;
